import React from 'react';
import { RootState } from '../states/store.ts';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setAnswer } from '../states/questionSlice.ts'; 

const QuestionCard = (props) => {
    const answerChoices = props.answerChoices;
    const dispatch = useDispatch(); 
    const currentChoice = useSelector((state) => state.question.totalChoices[props.title]);
    [classNames, setClassNames] = useState({})
    let tempClassNames = classNames
    Object.entries(answerChoices).forEach(([key, value]) => {
        if (currentChoice !== value) {
            tempClassNames[key] = 'choice'
        } else {
            tempClassNames[key] = 'choice-selected'
        }
    })
    setClassNames(tempClassNames)
    const Click = (k) => {
        // let old = currentChoice
        dispatch(setAnswer({prompt: props.title, choice: k})); 
        classNames[k] = 'choice-selected'
        console.log(currentChoice)
        console.log(classNames)
    }

    useEffect(() => {

    }, [currentChoice])

    return (
        <li className="question-card">
            <div id="prompt"><b>{props.title}</b></div>
            <ul>
                {Object.entries(answerChoices).map(([key, value]) => (
                    <li class={classNames[key]} key={key} onClick={() => Click(key)}>
                        {value}
                    </li>
                ))}
            </ul>
        </li>
    );
}

export default QuestionCard;
