/* eslint-disable */
import React, { useState, useEffect } from 'react';
import QuestionCard from './QuestionCard';
import Data from '../data.json';
import Typewriter from 'typewriter-effect';
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux';

const Home = () => {
    const choices = useSelector((state) => state.question.totalChoices);
    const onClick = () => {
        console.log()
        if (Object.keys(choices).length === Data.questions.length) {
            window.location.assign("/results");
        }
    }

    return (
        <div className="App">
            {/* 1. Header */}
            <h1 id="title">
                <Typewriter options={{
                    strings: ['Quiz: Do Your Parents Miss You or Do They Just Feel Obligated to Talk to You?', 'Quiz: Do Your Parents Hate You?', 'Quiz: Do You Hate Your Parents?'],
                    autoStart: true,
                    loop: true,
                }}/>
            </h1>
            {/* 2. Question Cards */}
            <div class="questions">
                {Data.questions.map(({ prompt, options }) => (
                    <QuestionCard title={prompt} answerChoices={options} />
                ))}
            </div>
            <div class="choice" id="submit">
                    <button onClick={onClick}><b>Show me my results!</b></button>
                </div>

        </div>
    );
}

export default Home;
