import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    totalChoices: new Map()
}

export const questionSlice = createSlice({
    name: "questions",
    initialState,
    reducers: {
        createNew: (state, action) => {
            state.totalChoices.set(action.payload, 1)
        },
        addOption: (state, action) => {
            state.totalChoices.set(action.payload, state.totalChoices.get(action.payload) + 1)
        },
        removeOption: (state, action) => {
            state.totalChoices.set(action.payload, state.totalChoices.get(action.payload) - 1)
        }
    }
})

export const { createNew, addOption, removeOption } = questionSlice.actions
export default questionSlice.reducer
