import React from 'react';
import { RootState } from '../states/store.ts';
import { useDispatch, useSelector } from 'react-redux';
import { setAnswer } from '../states/questionSlice.ts'; 

const QuestionCard = (props) => {
    const prompt = props.title;
    const answerChoices = props.answerChoices;
    const dispatch = useDispatch(); 
    const totalChoices = useSelector((state) => state.question.totalChoices.get(1))

    const Click = () => {
        dispatch(setAnswer({number: 1, choice: "B"})); 
    }
    return (
        <li className="question-card">
            <div id="prompt"><b>{prompt}</b></div>
            <ul>
                {Object.entries(answerChoices).map(([key, value]) => (
                    <li class="choice" key={key} onClick={Click}>
                        {value}
                    </li>
                ))}
            </ul>
        </li>
    );
}

export default QuestionCard;
