import './App.css';
import React, { useState, useEffect } from 'react';
import QuestionCard from './components/QuestionCard';
import Data from './data.json';
import Typewriter from 'typewriter-effect';

function App() {
  const [time, setTime] = useState(new Date().toLocaleTimeString());
  const [answered, setAnswered] = useState(0);
  const [displayResults, setDisplayResults] = useState(false);
  const Confettiful = function(el) {
    this.el = el;
    this.containerEl = null;

    this.confettiFrequency = 3;
    this.confettiColors = ['#EF2964', '#00C09D', '#2D87B0', '#48485E','#EFFF1D'];
    this.confettiAnimations = ['slow', 'medium', 'fast'];

    this._setupElements();
    this._renderConfetti();
  };

  Confettiful.prototype._setupElements = function() {
    const containerEl = document.createElement('div');
    const elPosition = this.el.style.position;

    if (elPosition !== 'relative' || elPosition !== 'absolute') {
      this.el.style.position = 'relative';
    }

    containerEl.classList.add('confetti-container');

    this.el.appendChild(containerEl);

    this.containerEl = containerEl;
  };

  Confettiful.prototype._renderConfetti = function() {
    this.confettiInterval = setInterval(() => {
      const confettiEl = document.createElement('div');
      const confettiSize = (Math.floor(Math.random() * 3) + 7) + 'px';
      const confettiBackground = this.confettiColors[Math.floor(Math.random() * this.confettiColors.length)];
      const confettiLeft = (Math.floor(Math.random() * this.el.offsetWidth)) + 'px';
      const confettiAnimation = this.confettiAnimations[Math.floor(Math.random() * this.confettiAnimations.length)];

      confettiEl.classList.add('confetti', 'confetti--animation-' + confettiAnimation);
      confettiEl.style.left = confettiLeft;
      confettiEl.style.width = confettiSize;
      confettiEl.style.height = confettiSize;
      confettiEl.style.backgroundColor = confettiBackground;

      confettiEl.removeTimeout = setTimeout(function() {
        confettiEl.parentNode.removeChild(confettiEl);
      }, 3000);

      this.containerEl.appendChild(confettiEl);
    }, 25);
  };

  window.confettiful = new Confettiful(document.querySelector('.js-container'));




  useEffect(() => {
    const intervalId = setInterval(() => {
      setTime(new Date().toLocaleTimeString());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="App js-container container">
      {/* 1. Header */}
      <h1 id="title"><Typewriter
        options={{
          strings: ['Quiz: Do Your Parents Miss You or Do They Just Feel Obligated to Talk to You?', 'Quiz: Do Your Parents Hate You?', 'Quiz: Do You Hate Your Parents?'],
          autoStart: true,
          loop: true,
        }}
      />
      </h1>
      {/* <h2> BY JOSH CAMPBELL
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <h3>{time}</h3>
      </h2> */}
      <div class="questions">
        <div class="question-card">
          <h2> CONGRATULATIONS! </h2>
          <div> Insert Result </div>

        </div>
      </div>

      {/* 2. Question Cards */}
      <div class="questions">
        {Data.questions.map(({ prompt, options }) => (
          <QuestionCard title={prompt} answerChoices={options} />
        ))}
      </div>
    </div>
  );
}

export default App;
