import React from 'react';
import { RootState } from '../states/store.ts';
import { useDispatch, useSelector } from 'react-redux';
import { createNew, addOption, removeOption } from '../states/questionSlice.ts'; 

const QuestionCard = (props) => {
    const title = props.title;
    const answerChoices = props.answerChoices;
    const dispatch = useDispatch(); 

    const Click = () => {
        dispatch(createNew("B")); 
        console.log(useSelector((state) => state.question.totalChoices.get("B")))
    }
    return (
        <tr className="question-card">
            <div>{title}</div>
            <ul>
                {Object.entries(answerChoices).map(([key, value]) => (
                    <li key={key} onClick={Click}>
                        {key}: {value}
                    </li>
                ))}
            </ul>
            <li v-for="question in questions">
    {{ title }}
    <ul>
      <li v-for="A in Object.values(answerChoices.options)">
        {{ A }}
      </li>
    </ul>
  </li>
        </tr>
    );
}

export default QuestionCard;
